import('./magnetic-cursor/index.js').then(module=>{const{MagneticCursorSystem}=module;window.magneticCursor=new MagneticCursorSystem({autoInit:true,enableTrail:true,enableButtons:true,enableLinks:true,performanceMode:'auto'});}).catch(err=>{});document.addEventListener('DOMContentLoaded',()=>{document.body.classList.add('liquid-transitions-enabled');const navLinks=document.querySelectorAll('nav a,.nav-link,.chapter-link');navLinks.forEach(link=>{link.classList.add('liquid-transition');});let isTransitioning=false;document.addEventListener('click',(e)=>{const link=e.target.closest('a');if(link&&link.href&&!link.href.includes('#')&&!isTransitioning){e.preventDefault();isTransitioning=true;document.body.classList.add('liquid-morph-out');setTimeout(()=>{window.location.href=link.href;},800);}});document.body.classList.add('liquid-morph-in');});